import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import TokenService from '../services/token.service';

export default function OnePost() {
    const [post, setPost] = useState(null);
    const { postId } = useParams();

    useEffect(() => {
        fetchPost(postId);
    }, [postId]);

    const fetchPost = async (id) => {
        try {
            const token = TokenService.getLocalAccessToken();
            const response = await fetch(`http://localhost:8080/api/posts/${id}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });
            if (response.ok) {
                const data = await response.json();
                setPost(data);
            } else {
                console.error('Failed to fetch post:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching post:', error.message);
        }
    };

    if (!post) {
        return <div>Loading...</div>;
    }


    return (
        <div className="container mt-5">
          <div className="row">
            <div className="col-lg-8">
              {post ? (
                <article>
                  <header className="mb-4">
                    <h1 className="fw-bolder mb-1">{post.title}</h1>
                    <div className="text-muted fst-italic mb-2">{`Posted on ${post.date} by ${post.author}`}</div>
                    {/* Check if categories exist before mapping */}
                    {post.categories && post.categories.map((category, index) => (
                      <a key={index} className="badge bg-secondary text-decoration-none link-light" href="#!">{category}</a>
                    ))}
                  </header>
                  <figure className="mb-4"><img className="img-fluid rounded" src={post.image} alt={post.title} /></figure>
                  <section className="mb-5">
                    {/* Check if content exists before mapping */}
                    {post.content && post.content.map((paragraph, index) => (
                      <p key={index} className="fs-5 mb-4">{paragraph}</p>
                    ))}
                  </section>
                </article>
              ) : (
                <div>Loading...</div>
              )}
              {/* Comments section */}
              {/* Add comments section here */}
            </div>
            {/* Side widgets */}
            {/* Add side widgets here */}
          </div>
        </div>
      );

}
