import React, { useState, useEffect } from "react";
import { Link, Outlet, useLocation, useParams } from "react-router-dom";
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';
import { deleteGood, getAllGoodsAndCat } from "./api";
import './index.css';

export default function Goods() {
    const location = useLocation();
    const params = useParams();

    const [categories, setCategories] = useState([]);
    const [goods, setGoods] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            let url = params.catId ? `http://localhost:4000/goods?categoryId=${params.catId}` : 'http://localhost:4000/goods';
            const [cats, goods] = await getAllGoodsAndCat(url);
            setCategories(cats);
            setGoods(goods);
        };
        fetchData();

        if (location.state) {
            NotificationManager.success(location.state.message, location.state.title, 3000);
        }
    }, [location, params.catId]);

    const handleDelete = async (goodId) => {
        const resp = await deleteGood(goodId);
        window.location.reload();
    };

    return (
        <div className="container mx-auto py-8">
            <h1 className="text-4xl mb-16">Выбери, кого хочешь завести!</h1>

            <div>
                <NotificationContainer />
                <div className="flex">
                    {categories.map((cat) => (
                        <div key={cat.id} className="card mr-4">
                            <Link to={`/goods/category/${cat.id}`} className="category-link">
                                <button className="bg-indira text-white font-semibold py-2 px-4 rounded-full">
                                    {cat.name}
                                </button>
                            </Link>
                        </div>
                    ))}
                </div>
                <hr />

                <div className="grid grid-cols-3 gap-4">
                    {goods.map((good) => (
                        <div key={good.id} className="card">
                            <Link to={`/goods/${good.id}`} className="good-link">
                                <h3 className="good-name">Good {good.name}</h3>
                            </Link>
                            <div className="flex justify-between mt-2">
                                <Link to={`/goods/${good.id}/edit`} className="edit-link">
                                    Edit
                                </Link>
                                <button onClick={() => handleDelete(good.id)} className="delete-btn">
                                    Delete
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
                <hr />
                <Outlet />
            </div>
        </div>
    );
}
