import { Link, useParams, useLoaderData } from "react-router-dom";

export default function GoodItem() {
  const good = useLoaderData();
  
  const IMG = (imgName) => {
    return require(`../images/${imgName}`);
  }

  console.log(good.img);

  return (
    <div className="container mx-auto py-8">
      <div className="grid grid-cols-4 gap-8"></div>
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
    <h1 className="text-4xl font-semibold"> {good.name} </h1>
      <h1 className="text-lg font-semibold"> {good.price} </h1>
      <h1 className="text-lg font-semibold"> {good.city} </h1>
      <br />
      <img src={IMG(good.img)} alt="Good Image" />
      <button><Link to="/goods">Close</Link></button>
    </div>
    </div>


  );
}
