import React, { useState } from 'react';
import '../styles/styles.css'; 
import animalsImage from '../images/animals.png';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();

    // Ваш код для валидации данных
    if (!email || !password) {
      setError('Email and password are required');
      return;
    }

    // Другие проверки, которые могут потребоваться

    // Если данные прошли валидацию, можно отправить запрос на сервер или выполнить другие действия
    console.log('Email:', email);
    console.log('Password:', password);

    // После успешной обработки данных сбрасываем ошибку
    setError(null);
  };

  return (
    <div className="max-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mb-2 text-center text-3xl text-black">Login</h2>
          <img src={animalsImage} alt="Animals" />
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <h2 className="text-2xl text-black mb-2">Email</h2>
            <input
              id="email-address"
              name="email"
              type="email"
              autoComplete="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-green-500 focus:border-green-500 focus:z-10 sm:text-sm"
              placeholder="Email address"
            />
          </div>
          <div>
            <h2 className="text-2xl text-black mb-2">Password</h2>
            <input
              id="password"
              name="password"
              type="password"
              autoComplete="current-password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-green-500 focus:border-green-500 focus:z-10 sm:text-sm"
              placeholder="Password"
            />
          </div>
          {error && <p className="text-red-500">{error}</p>}
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                id="remember-me"
                name="remember-me"
                type="checkbox"
                className="h-4 w-4 text-green-600 focus:ring-green-500 border-gray-300 rounded"
              />
              <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-900">
                Remember me
              </label>
            </div>
            <div className="text-sm">
              <a href="#" className="font-medium text-green-600 hover:text-green-500">
                Forgot your password?
              </a>
            </div>
          </div>
          <div>
            <button type="submit" className="bg-green-600 text-white text-sm w-full py-2 rounded-md">
              Login
            </button>
          </div>
          <div>
            <button type="button" className="bg-white text-black border-solid border-green-600 rounded-md hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-green-600 focus:ring-opacity-50 w-full py-2">
              Register
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
