import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import TokenService from '../services/token.service';

export default function OnePost() {
    const [post, setPost] = useState(null);
    const { postId } = useParams();

    useEffect(() => {
        fetchPost(postId); 
    }, [postId]);

    const fetchPost = async (id) => {
        try {
            const token = TokenService.getLocalAccessToken();
            const response = await fetch(`http://localhost:8080/api/posts/${id}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });
            if (response.ok) {
                const data = await response.json();
                setPost(data);
            } else {
                console.error('Failed to fetch post:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching post:', error.message);
        }
    };

    const handleDelete = async () => {
        try {
            const token = TokenService.getLocalAccessToken();
            const response = await fetch(`http://localhost:8080/api/posts/${postId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });
            if (response.ok) {
                console.log('Post deleted successfully');
                // Redirect to a different page or take appropriate action after deleting the post
            } else {
                console.error('Failed to delete post:', response.statusText);
            }
        } catch (error) {
            console.error('Error deleting post:', error.message);
        }
    };

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-lg-8">
                    {/* Post content */}
                    <article>
                        {/* Post header */}
                        <header className="mb-4">
                            {/* Post title */}
                            <h1 className="fw-bolder mb-1">{post.title}</h1>
                            {/* Post meta content */}
                            <div className="text-muted fst-italic mb-2">{post.createdAt}</div>
                            {/* Post categories */}
                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Web Design</a>
                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Freebies</a>
                        </header>
                        {/* Preview image figure */}
                        <figure className="mb-4"><img className="img-fluid rounded" src={post.photo} alt="..." /></figure>
                        {/* Post content */}
                        <section className="mb-5">
                            <p className="fs-5 mb-4">{post.description}</p>
                            <p className="fs-5 mb-4">Author: {post.userId}</p> {/* Display the author's username */}
                            <button className="btn btn-primary me-2" onClick={() => handleEdit(post.id)}>Edit</button>
                            <button className="btn btn-danger" onClick={handleDelete}>Delete</button>
                        </section>
                    </article>
                    {/* Comments section */}
                    {/* Comments section */}
                    {/* Comments section */}
                </div>
                {/* Side widgets */}
                {/* Side widgets */}
                {/* Side widgets */}
            </div>
        </div>
    );
}
