import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";
import { Link } from "react-router-dom";

export default class Sidebar extends Component {
    constructor(props) {
        super(props);
    
        this.state = {
          redirect: null,
          userReady: false,
          currentUser: { username: "" }
        };
      }
    
      componentDidMount() {
        const currentUser = AuthService.getCurrentUser();
    
        if (!currentUser) this.setState({ redirect: "/home" });
        this.setState({ currentUser: currentUser, userReady: true })
      }
    
      render() {
        // if (this.state.redirect) {
        //   return <Navigate to={this.state.redirect} />
        // }
    
        const { currentUser } = this.state;
    return (

<div className="d-flex ">
          <div className="container-fluid">
            <a className="navbar-brand" href="#">Offcanvas navbar</a>
            <div className="offcanvas offcanvas-start  show" id="offcanvasNavbar" data-bs-backdrop="false" aria-labelledby="offcanvasNavbarLabel">
              
              <div className="offcanvas-body">
                <ul className="navbar-nav justify-content-end flex-grow-1 pe-3">
                  <li className="nav-item">
                  <Link to = {"/Home"}>Главная страница</Link> </li>
                  <li className="nav-item">
                  <Link to="/profile" className="nav-link active" aria-current="page">Account Details</Link>
                  </li>
                  <li className="nav-item">
                    <a className="nav-link" href="#">Posts</a>
                  </li>
                  <li className="nav-item">
                  {currentUser ? (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/profile"} className="nav-link">
                  {currentUser.username}
                </Link>
              </li>
              <li className="nav-item">
                <a href="/login" className="nav-link" onClick={this.logOut}>
                  LogOut
                </a>
              </li>
            </div>
          ) : (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/login"} className="nav-link">
                  Login
                </Link>
              </li>

              <li className="nav-item">
                <Link to={"/register"} className="nav-link">
                  Sign Up
                </Link>
              </li>
            </div>
          )}

        
        </li>
                </ul>
              </div>
            </div>
          </div>
          </div>
    )
}
}
