import React, { useState } from 'react';
import '../styles/styles.css'; 
import animalsImage from '../images/animals.png';
import { Link } from 'react-router-dom';


export default function Registration() {
  const [regData, setRegData] = useState({
    username: '',
    email: '',
    password: '',
    role: ''

  })
  
  const handleChange = (e) => {
    setRegData({ ...regData, [e.target.name]: e.target.value})
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8080/api/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(regData)
      });
      if (response.ok) {
        console.log('ok');
      } else {
        console.error('fail');
      }
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <div className="max-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className=" mb-2 text-center text-3xl text-black">Registration</h2>
          <img src={animalsImage} alt="Animals" />
        </div>
        <div>
        <h2 className=" text-2xl text-black mb-4">Username</h2>
              <label className="sr-only">Username</label>
              <input id="role" value={regData.role} onChange={handleChange} name="role" type="text"
    autoComplete="off"
    required className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-green-500 focus:border-green-500 focus:z-10 sm:text-sm" placeholder="Username" />
            </div>
        <div>
        <h2 className=" text-2xl text-black mb-4">Email</h2>
              <label htmlFor="email-address" className="sr-only">Email address</label>
              <input id="email" value={regData.email} onChange={handleChange}  name = "email" type="email" required className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-green-500 focus:border-green-500 focus:z-10 sm:text-sm" placeholder="Email address" />
            </div>
            <div >
            <h2 className=" text-2xl text-black mb-4">Password</h2>
              <label className="sr-only">Password</label>
              <input id="password" value={regData.password} onChange={handleChange} type="text" required className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-green-500 focus:border-green-500 focus:z-10 sm:text-sm" placeholder="Password" />
            </div>
            <div >
            <h2 className=" text-2xl text-black mb-4">Role</h2>
              <label htmlFor="email-address" className="sr-only">Role</label>
              <input id="role" value={regData.role} onChange={handleChange} type="text" autoComplete="email" required className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-green-500 focus:border-green-500 focus:z-10 sm:text-sm" placeholder="Role" />
            </div>

            
            <div className='flex flex-row-reverse'>
              <button className='bg-indira text-white text-sm'>

                <Link to="/registration" />Register
              </button>
            </div>
        
      </div>
    </div>
  );
}
