import React, { useEffect } from 'react';

import { useState } from 'react';
import { getOneGood, saveGood, updateGood } from './api';
import 'react-notifications/lib/notifications.css';
import {NotificationContainer, NotificationManager} from 'react-notifications';
import {useNavigate, useParams} from 'react-router-dom'

export default function EditForm() {

    const navigate = useNavigate();
    const params = useParams();

    useEffect(() => {
        const getGood = async () => {
            const resp = await getOneGood(params.goodId);
            setGood(resp)
        }
      getGood()
    }, [])

    const [good, setGood] = useState({
        name: '',
        city: '',
        price: 0
    });

    const handleName = (ev) => { setGood({ ...good, name: ev.target.value }) };
    const handleCity = (ev) => { setGood({ ...good, city: ev.target.value }) };
    const handlePrice = (ev) => { setGood({ ...good, price: parseInt(ev.target.value) }) };

    const handleSubmit = async (ev) => {
        ev.preventDefault();
        const updatedGood = await updateGood(good);
        navigate("/goods", {state: { message: updatedGood.name + " updated ", title: "Updated good" }})
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                name: <input type="text" value={good.name} onChange={handleName} /> <br />
                city: <input type="text" value={good.city} onChange={handleCity} /> <br />
                price: <input type="number" value={good.price} onChange={handlePrice} /> <br />

                <button type="submit">
                    Save
                </button>
            </form>
        </div>
    );
}
