import React, { useState } from 'react';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({ email: '', password: '' });
  const [isLogin, setIsLogin] = useState(true);

  const validateForm = () => {
    let isValid = true;
    const newErrors = {};

    if (!email) {
      newErrors.email = 'Email is required';
      isValid = false;
    }

    if (!password) {
      newErrors.password = 'Password is required';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validateForm()) {
      // Здесь можно добавить логику для отправки данных на сервер для входа или регистрации
      if (isLogin) {
        // Логика для входа
      } else {
        // Логика для регистрации
      }
    }
  };

  return (
    <div className="max-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mb-2 text-center text-3xl text-black">{isLogin ? 'Login' : 'Register'}</h2>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <h2 className="text-2xl text-black mb-1">Email</h2>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className={`input-field ${errors.email && 'border-red-500'}`}
              placeholder="Enter your email"
              required
            />
            {errors.email && <p className="text-red-500">{errors.email}</p>}
          </div>
          <div>
            <h2 className="text-2xl text-black mb-1">Password</h2>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className={`input-field ${errors.password && 'border-red-500'}`}
              placeholder="Enter your password"
              required
            />
            {errors.password && <p className="text-red-500">{errors.password}</p>}
          </div>
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                type="checkbox"
                className="checkbox"
                id="rememberMe"
              />
              <label htmlFor="rememberMe" className="text-sm font-medium text-black dark:text-gray-300">
                Remember me
              </label>
            </div>
            <div>
              <button type="submit" className="btn-primary">
                {isLogin ? 'Login' : 'Register'}
              </button>
            </div>
          </div>
        </form>
        <div className="text-center">
          <button
            onClick={() => setIsLogin(!isLogin)}
            className="text-green-800 hover:underline"
          >
            {isLogin ? 'Create an account' : 'Already have an account? Login'}
          </button>
        </div>
      </div>
    </div>
  );
}
