import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import TokenService from '../services/token.service';

export default function OnePost() {
    const [post, setPost] = useState(null);
    const { postId } = useParams();

    useEffect(() => {
        fetchPost(postId);
    }, [postId]);

    const fetchPost = async (id) => {
        try {
            const token = TokenService.getLocalAccessToken();
            const response = await fetch(`http://localhost:8080/api/posts/${id}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                }
            });
            if (response.ok) {
                const data = await response.json();
                setPost(data);
            } else {
                console.error('Failed to fetch post:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching post:', error.message);
        }
    };

    const fetchUserName = async (userId) => {
        try {
            const response = await fetch(`http://localhost:8080/api/users/${userId}`);
            if (response.ok) {
                const user = await response.json();
                return user.username;
            } else {
                console.error('Failed to fetch user:', response.statusText);
                return 'Unknown User';
            }
        } catch (error) {
            console.error('Error fetching user:', error.message);
            return 'Unknown User';
        }
    };

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-lg-8">
                    {/* Post content */}
                    <article>
                        {/* Post header */}
                        <header className="mb-4">
                            {/* Post title */}
                            <h1 className="fw-bolder mb-1">{post.title}</h1>
                            {/* Post meta content */}
                            <div className="text-muted fst-italic mb-2">{post.createdAt}</div>
                            {/* Post categories */}
                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Web Design</a>
                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Freebies</a>
                        </header>
                        {/* Preview image figure */}
                        <figure className="mb-4"><img className="img-fluid rounded" src={post.photo} alt="..." /></figure>
                        {/* Post content */}
                        <section className="mb-5">
                            <p className="fs-5 mb-4">{post.description}</p>
                            <p className="fs-5 mb-4">Author: {post.userId}</p>
                        </section>
                    </article>
                    {/* Comments section */}
                    <section className="mb-5">
                        <div className="card bg-light">
                            <div className="card-body">
                                {/* Comment form */}
                                <form className="mb-4"><textarea className="form-control" rows="3" placeholder="Join the discussion and leave a comment!"></textarea></form>
                                {/* Comment with nested comments */}
                                {/* Loop through comments */}
                                {post.comments.map((comment, index) => (
                                    <div key={index} className="d-flex mb-4">
                                        {/* Parent comment */}
                                        <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                        <div className="ms-3">
                                            <div className="fw-bold">{fetchUserName(comment.userId)}</div>
                                            {comment.content}
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </section>
                </div>
                {/* Side widgets */}
                {/* Add side widgets here */}
            </div>
        </div>
    );
}
