 import { Link, Outlet, useLoaderData, useLocation, useParams} from "react-router-dom";
import 'react-notifications/lib/notifications.css';
import { useEffect, useState } from "react";
import {NotificationContainer, NotificationManager} from 'react-notifications';
import { deleteGood, getAllGoodsAndCat } from "./api";

export default function Goods(){
    // const allGoods = useLoaderData();
    const location = useLocation();
    const params = useParams();

    console.log(params)
    const [categories, setCategories] = useState([]);
    const [goods, setGoods] = useState([]);

    useEffect(() => {
        let url = params.catId ? 'http://localhost:4000/goods?categoryId='+params.catId : 'http://localhost:4000/goods'
        const getAllData = async () => {
        const [cats, goods] = await getAllGoodsAndCat(url);
        setCategories(cats)
        setGoods(goods)

    }
    getAllData()

        if(location.state)
            NotificationManager.success(location.state.message, location.state.title, 3000);
    }, [location])

    const handleClick = async (goodId) => {
        const resp = await deleteGood(goodId)
        window.location.reload()
    }

    return(
        <div className='container mx-auto py-8'>
                  <h1 className='text-4xl mb-16'>Выбери, кого хочешь завести!</h1>


        <div>
            <NotificationContainer />
            <div className='flex'>
            {

                categories.map((cat) => {
                    return (
                        <div key = {cat.id}>
                            <Link to = {'/goods/category/'+cat.id}>
                            <button className="bg-indira hover:bg-green-600 text-white font-semibold py-2 px-4 rounded-full mr-4"> Good {cat.name}></button>
                            </Link>
        
                            </div>
                    )
                })
            }
            </div>
            <hr />
            {
                goods.map((good) => {
                    return (
                        <div key = {good.id}>
                            <Link to = {'/goods/'+good.id}>
                                Good {good.name}
                            </Link>
                            &nbsp; | &nbsp;
                            <Link to = {'/goods/'+good.id + '/edit'}>
                                Edit
                            </Link>
                            &nbsp; | &nbsp;
                            <button onClick={() => handleClick(good.id)}>Delete</button>

                            </div>
                    )
                })
            }

            <hr />
            <Outlet />
        </div>
        </div>
    )
}
