import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function OnePost() {
    const [postData, setPostData] = useState(null);
    const [username, setUsername] = useState(null); // State to store the username
    const { postId } = useParams();

    useEffect(() => {
        fetchPost(postId); 
    }, [postId]);

    const fetchPost = async (id) => {
        try {
            const response = await axios.get(`http://localhost:8080/api/posts/${id}`);
            if (response.ok) {
                const postData = response.data;
                setPostData(postData);
                // Fetch username associated with the userId
                fetchUsername(postData.userId);
            } else {
                console.error('Failed to fetch post:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching post:', error.message);
        }
    };

    const fetchUsername = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8080/api/user/${userId}`);
            if (response.ok) {
                const user = response.data;
                setUsername(user.username);
            } else {
                console.error('Failed to fetch user:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching user:', error.message);
        }
    };

    if (!postData || !username) {
        return <div>Loading...</div>;
    }

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-lg-8">
                    {/* <!-- Post content--> */}
                    <article>
                        {/* <!-- Post header--> */}
                        <header className="mb-4">
                            {/* <!-- Post title--> */}
                            <h1 className="fw-bolder mb-1">{postData.title}</h1>
                            {/* <!-- Post meta content--> */}
                            <div className="text-muted fst-italic mb-2">{postData.createdAt}</div>
                            {/* <!-- Post categories--> */}
                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Web Design</a>
                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Freebies</a>
                        </header>
                        {/* <!-- Preview image figure--> */}
                        <figure className="mb-4"><img className="img-fluid rounded" src={postData.photo} alt="..." /></figure>
                        {/* <!-- Post content--> */}
                        <section className="mb-5">
                            <p className="fs-5 mb-4">{postData.description}</p>
                            <p className="fs-5 mb-4">Author: {postData.username}</p>
                        </section>
                    </article>
                    {/* <!-- Comments section--> */}
                    <section className="mb-5">
                        <div className="card bg-light">
                            <div className="card-body">
                                {/* <!-- Comment form--> */}
                                <form className="mb-4"><textarea className="form-control" rows="3" placeholder="Join the discussion and leave a comment!"></textarea></form>
                                {/* <!-- Comment with nested comments--> */}
                                <div className="d-flex mb-4">
                                    {/* <!-- Parent comment--> */}
                                    <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                    <div className="ms-3">
                                        <div className="fw-bold">Commenter Name</div>
                                        If you're going to lead a space frontier, it has to be government; it'll never be private enterprise. Because the space frontier is dangerous, and it's expensive, and it has unquantified risks.
                                        {/* <!-- Child comment 1--> */}
                                        <div className="d-flex mt-4">
                                            <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                            <div className="ms-3">
                                                <div className="fw-bold">Commenter Name</div>
                                                And under those conditions, you cannot establish a capital-market evaluation of that enterprise. You can't get investors.
                                            </div>
                                        </div>
                                        {/* <!-- Child comment 2--> */}
                                        <div className="d-flex mt-4">
                                            <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                            <div className="ms-3">
                                                <div className="fw-bold">Commenter Name</div>
                                                When you put money directly to a problem, it makes a good headline.
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                {/* <!-- Single comment--> */}
                                <div className="d-flex">
                                    <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                    <div className="ms-3">
                                        <div className="fw-bold">Commenter Name</div>
                                        When I look at the universe and all the ways the universe wants to kill us, I find it hard to reconcile that with statements of beneficence.
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                </div>
                {/* <!-- Side widgets--> */}
                <div className="col-lg-4">
                    {/* <!-- Search widget--> */}
                    <div className="card mb-4">
                        <div className="card-header">Search</div>
                        <div className="card-body">
                            <div className="input-group">
                                <input className="form-control" type="text" placeholder="Enter search term..." aria-label="Enter search term..." aria-describedby="button-search" />
                                <button className="btn btn-primary" id="button-search" type="button">Go!</button>
                            </div>
                        </div>
                    </div>
                    {/* <!-- Categories widget--> */}
                    <div className="card mb-4">
                        <div className="card-header">Categories</div>
                        <div className="card-body">
                            <div className="row">
                                <div className="col-sm-6">
                                    <ul className="list-unstyled mb-0">
                                        <li><a href="#!">Web Design</a></li>
                                        <li><a href="#!">HTML</a></li>
                                        <li><a href="#!">Freebies</a></li>
                                    </ul>
                                </div>
                                <div className="col-sm-6">
                                    <ul className="list-unstyled mb-0">
                                        <li><a href="#!">JavaScript</a></li>
                                        <li><a href="#!">CSS</a></li>
                                        <li><a href="#!">Tutorials</a></li>
                                    </ul>
                                </div>
