import { Link, useParams, useLoaderData } from "react-router-dom";

export default function GoodItem() {
  const good = useLoaderData();
  
  const IMG = (imgName) => {
    return require(`../images/${imgName}`);
  }

  console.log(good.img);

  return (
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        {/* <h1 className="text-4xl font-semibold">{good.name}</h1> */}
        <h1 className="text-lg font-normal">Price: {good.price}</h1>
        <h1 className="text-lg font-normal">City: {good.city}</h1>
        <img className="mt-4" src={IMG(good.img)} alt="Good Image" style={{ maxWidth: "300px" }} />
        <button className="bg-indira py-2 px-4 round-full"><Link to="/goods">Close</Link></button>
      </div>
  );
}
